import { useQuery, gql } from "@apollo/client";

const GET_LOCATIONS = gql`
  query Characters {
    characters {
      results {
        episode {
          characters {
            id
            image
          }
        }
      }
    }
  }
`;

function DisplayLocations() {
  const { loading, error, data } = useQuery(GET_LOCATIONS);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  // Check if data.characters.results exists and is an array
  const characters = data.characters.results;
  if (!Array.isArray(characters)) {
    return <p>No character data found.</p>;
  }

  return (
    <div>
      {characters.map((character) => (
        <div key={character.id}>
          {character.episode &&
            character.episode.characters &&
            Array.isArray(character.episode.characters) &&
            character.episode.characters.map((char) => (
              <div key={char.id}>
                <img src={char.image} alt={char.id} />
              </div>
            ))}
        </div>
      ))}
    </div>
  );
}

export default function App() {
  return (
    <div>
      <h2>My first Apollo app ðŸš€</h2>
      <br />
      <DisplayLocations />
    </div>
  );
}
