import { useQuery, gql } from "@apollo/client";

const GET_LOCATIONS = gql`
query Characters {
  characters {
    results {
      episode {
        characters {
          id
          image
        }
      }
    }
  }
}
`;

function DisplayLocations() {
  // Define the characterId variable and pass it to the useQuery hook
  const characterId = "123"; // Replace with the actual character ID
  const { loading, error, data } = useQuery(GET_LOCATIONS)

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  // Ensure that data.character exists before accessing its properties
  if (!data || !data.character) {
    return <p>No character data found</p>;
  }

  const character = data.character;

  return (
    <div>
      <h1>Character Details</h1>
      <p>Air Date: {character.episode.air_date}</p>
      <h2>Related Characters:</h2>
      {character.episode.characters.map((relatedCharacter) => (
        <div key={relatedCharacter.id}>
          <img src={relatedCharacter.image} alt={relatedCharacter.id} />
          <p>Created: {relatedCharacter.created}</p>
        </div>
      ))}
    </div>
  );
}

export default function App() {
  return (
    <div>
      <h2>My first Apollo app ðŸš€</h2>
      <br />
      <DisplayLocations />
    </div>
  );
}
